        @autobind
        renderNodes(nodes, parentInFilter){

          let filters = this.state.text.split('>');
          // reverse filters when checking
          filters.reverse();

          nodes = universe.lodash.sortBy(nodes, 'name');
          // console.log('Nodes',nodes);

          return (
            <div className="">
              {
                nodes.map(node=>{

                  // include this node if it, or of of it's children, matches
                  // - highlight if a match 

                  const nodeMatchFunc = (node) => {
                    // returns true if this node is a "final match" 

                    function checkNodeFilterMatch(tmpCheckNode, filter){
                      let nodeFilterMatch;
                      let tmpFilter;

                      // default: path searching! 
                      // - no ":" in search input 
                      if(filter.indexOf(':') === -1 && tmpCheckNode._path){
                        tmpFilter = filter; //.split('p:')[1];
                        if(tmpCheckNode._path && tmpFilter.length && pathMatch(tmpCheckNode._path, tmpFilter)){
                          nodeFilterMatch = true;
                        }
                      }


                      // all (a:)
                      if(filter.substring(0,2) == 'a:'){
                        tmpFilter = filter.split('a:')[1];
                        if(tmpCheckNode._id && tmpCheckNode._id.toString().includes(tmpFilter)){
                          nodeFilterMatch = true;
                        }
                        if(tmpCheckNode.type && tmpCheckNode.type.includes(tmpFilter)){
                          nodeFilterMatch = true;
                        }
                        if(tmpCheckNode.name && tmpCheckNode.name.includes(tmpFilter)){
                          nodeFilterMatch = true;
                        }
                        if(tmpCheckNode.data && JSON.stringify(tmpCheckNode.data).includes(tmpFilter)){
                          nodeFilterMatch = true;
                        }
                      }
                      // type (t:)
                      if(filter.substring(0,2) == 't:' && tmpCheckNode.type){
                        tmpFilter = filter.split('t:')[1];
                        if(tmpCheckNode.type && tmpCheckNode.type.includes(tmpFilter)){
                          nodeFilterMatch = true;
                        }
                      }
                      // name (n:)
                      if(filter.substring(0,2) == 'n:' && tmpCheckNode.name){
                        tmpFilter = filter.split('n:')[1];
                        if(tmpCheckNode.name && tmpCheckNode.name.includes(tmpFilter)){
                          nodeFilterMatch = true;
                        }
                      }
                      // path (p:)
                      if(filter.substring(0,2) == 'p:' && tmpCheckNode._path){
                        tmpFilter = filter.split('p:')[1];
                        if(tmpCheckNode._path && tmpFilter.length && pathMatch(tmpCheckNode._path, tmpFilter)){
                          nodeFilterMatch = true;
                        }
                      }
                      // data (d:) 
                      if(filter.substring(0,2) == 'd:'){
                        tmpFilter = filter.split('d:')[1];
                        if(tmpCheckNode.data && JSON.stringify(tmpCheckNode.data).includes(tmpFilter)){
                          nodeFilterMatch = true;
                        }
                      }
                      // sibling type (st:) 
                      if(filter.substring(0,3) == 'st:' ){
                        tmpFilter = filter.split('st:')[1];
                        try {
                          let findSiblingMatch = tmpCheckNode.parent.nodes.find(n=>{
                            if(n.type && n.type.includes(tmpFilter)){
                              return true;
                            }
                          });
                          if(findSiblingMatch){
                            nodeFilterMatch = true;
                          }
                        }catch(err){
                          // console.error(err);
                        }
                      }
                      // dirtyJson (obj matcher)
                      if(filter.substring(0,1) == '{' && filter.substring(filter.length - 1) == '}'){
                        try {
                          let parsedJson = universe.dirtyJSON.parse(filter);
                          if(universe.lodash.query([tmpCheckNode.data],parsedJson).length){
                            nodeFilterMatch = true;
                          }
                        }catch(err){
                          // console.error(err);
                        }
                      }
                      return nodeFilterMatch;
                    }

                    // iterate through filters (TODO) 
                    // - find next parent in chain that matches 
                    let filtersClone = filters.concat();
                    let lastFilter = filtersClone.shift().trim();

                    let filtersToPass = lastFilter.split('&');

                    let passedAll = true;
                    for(let ftp of filtersToPass){

                      ftp = ftp.trim();

                      if(checkNodeFilterMatch(node, ftp)){
                        // matches the last check, see if parents need to be checked 
                        if(filtersClone.length){
                          // has a parent chain to check
                          console.log('has parent chain to check!!');
                          // return true;
                        } else {
                          // no other filters, dont need to check parent chain 
                          // return true;
                        }
                      } else {
                        passedAll = false;
                      }
                    }

                    if(passedAll){
                      return true;
                    }

                    return null;

                  }

                  const childMatchFunc = (nodes) => {
                    for(let tmpNode of nodes){
                      if(nodeMatchFunc(tmpNode)){
                        return true;
                      }
                      if(childMatchFunc(tmpNode.nodes)){
                        return true;
                      }
                    }
                    return false;
                  }

                  let hasFilter = (filters.length && filters[0].length) ? true:false;
                  let nodeMatch;
                  let childMatch;
                  if(hasFilter){

                    nodeMatch = nodeMatchFunc(node);
                    childMatch = childMatchFunc(node.nodes || []);

                    // check children 
                    // - lots of recursion here, probably should memoize 


                    // // filter by "text" if set 
                    // nodes = nodes.filter(node=>{
                    //   return true;
                    // })
                  }

                  if(hasFilter && !nodeMatch && !childMatch && !parentInFilter){
                    return undefined; // skip
                  }

                  // DO NOT SHOW CHILDREN (TODO: checkbox toggle?) 
                  if(!this.state.showChildren){
                    if(hasFilter && parentInFilter && !childMatch && !nodeMatch){
                      // console.log('Not showing cuz parentInFilter w/o this or child matching');
                      return undefined;
                    }
                  }

                  let tooltip = null;
                  // return a different tooltip depending on the type
                  let nodeTypeFirst = node.type ? node.type.split(':')[0]:null;
                  try {
                    switch(nodeTypeFirst){
                      case 'react_component':
                        tooltip = node.data.internalId;
                        break;

                      case 'parent_of_nodes_to_learn':
                        tooltip = node.data.learn;
                        break;

                      case 'text_tag':
                        tooltip = node.data.text;
                        break;

                      case 'capability':
                        tooltip = node.data.key;
                        break;

                      case 'code':
                        tooltip = node.data.note;
                        break;

                      case 'external_identity':
                        tooltip = node.data.commonName || '(no commonName)';
                        break;

                      case 'external_identity_connect_method':
                        tooltip = node.data.connection;
