                      case 'node_repo_on_github':
                        tooltip = node.data.repo;
                        break;

                      case 'app_base':
                        tooltip = [node.data.name, node.data.appId].join(' - ');
                        break;

                      case 'platform_nodes':
                        tooltip = node.data.platform;
                        break;

                      default:
                        break;
                    }
                  }catch(err){
                  }

                  let rowStyle = {whiteSpace:'nowrap'};
                  if(this.props.state.editingNode && this.props.state.editingNode._id == node._id){
                    rowStyle.fontWeight = 'bold';
                  }

                  if(this.props.state.selectedNodes && this.props.state.selectedNodes.find(n=>{return n._id == node._id})){
                    rowStyle.background = 'red';
