(()=>{
  return new Promise(async (resolve,reject)=>{
    try {

      const AceEditor = universe.AceEditor;
      const ReactResizeDetector = universe.ReactResizeDetector;
      const RecreateChildOnPropsChange = universe.RecreateChildOnPropsChange;

      const Link = universe.ReactRouterDomLink;

      class EmbeddedComponent extends React.Component {
        constructor(props){
          super(props);
        }

        render(){
          return (
            <div>
              embeddedComponenthere
            </div>
          )
        }
      }

      class ErrorComponent extends React.Component {
        constructor(props){
          super(props);
        }
        render(){
          return (
            <div>
              ErrorLoading
            </div>
          )
        }
      }

      let defaultState = {
        type: 'types.second.default.request.input',
        data: {
          auth: {
            token: '',
            secret: '', // optional
            'visitor-username' : '' // optional
          },
          extras: {
          },
          serviceName: 'services.second.default.get',
          actionPath: 'data',
          inputNode: {}
        }
      }

      class mycomponent extends React.Component {
        constructor(props){
          super(props);
          this.state = {
            value: JSON.stringify(defaultState,null,2),
            output: 'output'
          }
        }

        componentDidMount(){
          console.log('NodeEditorComponent componentDidMount');
          this.resizeAce();
          this.handleKeyDown();

        }

        componentWillUnmount(){

        }

        componentWillReceiveProps(nextProps, nextState){

          // resizing helpers for Ace height
          if(!nextProps.state.newWindowSize || 
            this.state.windowHeight != nextProps.state.newWindowSize[1]){
            this.setState({
              aceHeight: 0
            },()=>{
              this.resizeAce();
            })
          }
          if(nextState.aceHeight == -1){
            this.setState({
              aceHeight: 0
            },()=>{
              this.resizeAce();
            })
          }
        }

        @autobind
        resizeAce(){
          if(!this.parentRef){
            this.setState({
              aceHeight: null
            })
            return;
          }
          // console.log('Resize ace', this.parentRef.offsetHeight);
          this.setState({
            aceHeight: this.parentRef.offsetHeight
          },()=>{
            if(this.aceRef){
              this.aceRef.editor.resize();
            }
          })
        }

        @autobind
        handleKeyDown(e){
          if(this.aceRef && this.aceRef.editor){
            this.aceRef.editor.commands.addCommand({
              name: 'save',
              bindKey: {
                win: 'Ctrl-S',
                mac: 'Command-S'
              },
              exec: (env, args, request)=>{
                // this.saveDataUpdates();
                console.log('command+s does nothing');
              }
            });

            // overwrite Cmd-D to same as sublime 
            this.aceRef.editor.commands.addCommand({
              name: 'selectMoreAfter2',
              bindKey: {
                win: 'Ctrl-D',
                mac: 'Command-D'
              },
              exec: (env, args, request)=>{
                // selectMoreAfter
                // console.log('selectMoreAfter!');
                this.aceRef.editor.execCommand("selectMoreAfter")
              }
            });
          }

        }

        @autobind
        handleSend(){
          return new Promise(async (resolve,reject)=>{

            let RequestNode;
            try {
              RequestNode = JSON.parse(this.state.value)
            }catch(err){
              alert('Invalid JSON');
              return;
            }

            universe.$.ajax({
              url: '/ai',
              method: 'post',
              contentType: 'application/json',
              data: JSON.stringify(RequestNode),
              success: result=>{

                console.log('/ai output:', result);

                if(typeof result != 'string'){
                  result = universe.cJSON.stringify(result,null,2);
                }

                this.setState({
                  output: result
                });

              },
              error: errr=>{
                WINDOW.alert('Failed output');
              }
            });

            // let findResponse = await universe.loadAndRunCapability('TalkToSecond',{},{
            //   type: 'standard_capability_action:0.0.1:local:298j291bs',
            //   data: {
            //     action: 'send',
            //     options: {
            //       ExternalIdentityNode: this.props.state.OwnerSecondExternalIdentityNode,
            //       RequestNode
            //     }
            //   }
            // });

          })
        }

        @autobind
        populateDefault(){

          let obj = defaultState;

          let value = JSON.stringify(obj, null, 2);
          this.setState({value})
        }

        render(){

          return (
            <div className="flex-main">
              <div className="flex-left" style={{padding:'4px', width: '450px'}}>

                <button className="button is-primary" onClick={this.handleSend}>
                  Send
                </button>

                <hr />

                <div style={{borderBottom: '1px solid #ccc'}} onClick={this.populateDefault}>
                  authorized action sequence, get Root nodes 
                </div>

              </div>
              <div className="flex-middle">

                <AceEditor
                  ref={r=>this.aceRef=r}
                  mode="jsx"
                  theme="monokai"
                  value={this.state.value}
                  onChange={v=>{
                    this.setState({value: v})
                  }}
                  name="UNIQUE_ID_OF_DIV2"
                  editorProps={{$blockScrolling: true}}
                  width="100%"
                  height="100%"
                  onLoad={editor=>{
                    editor.getSession().setUseWrapMode(true);
                  }}
                  setOptions={{
                    enableBasicAutocompletion: true,
                    enableLiveAutocompletion: true,
                    showPrintMargin: false,
                    enableSnippets: true,
                    behavioursEnabled: false,
                    showLineNumbers: true,
                    tabSize: 2
                  }}
                />

              </div>
              <div className="flex-middle">

                <AceEditor
                  ref={r=>this.aceRef=r}
                  mode="jsx"
                  theme="monokai"
                  value={this.state.output}
                  name="UNIQUE_ID_OF_DIV3"
                  editorProps={{$blockScrolling: true}}
                  width="100%"
                  height="100%"
                  onLoad={editor=>{
                    editor.getSession().setUseWrapMode(true);
                  }}
                  setOptions={{
                    enableBasicAutocompletion: true,
                    enableLiveAutocompletion: true,
                    showPrintMargin: false,
                    enableSnippets: true,
                    behavioursEnabled: false,
                    showLineNumbers: true,
                    tabSize: 2
                  }}
                />

              </div>

            </div>
          )
        }
      }

      mycomponent = universe.ReactGlobalState(mycomponent);

      resolve({
        type: 'react_component:0.0.1:local:98912hd89',
        data: mycomponent
      });

    }catch(err){
      reject({
        ERROR: true, 
        err: err.toString()
      });
    }

  })


})()
